import React, {useState, useEffect} from "react";
import Header from "./Header";
import Footer from "./Footer";
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import qs from 'qs';

function ApplicationForm(){

    const [newValue, setNewValue] = useState({
        username: "",
        email: "",
        staffid: "",
        post: "",
        dept: "",
        course: ""
    });

    const [couseList, setCourseList] = useState([{}]);

    const fields = ["姓名", "電郵", "職員編號", "職位", "單位", "報讀課程"];
    var role = "";

    const url = 'http://localhost:5000/user';
        const options = {
            method: 'POST',
            headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/x-www-form-urlencoded'
            },
            body: qs.stringify({username: localStorage.getItem('username')})
        };

        fetch(url, options)
        .then(res => res.json())
        .then(res => {
            role = res.role;
            setNewValue({
                username: res.name,
                email: res.username,
                staffid: res.staffid,
                post: res.post,
                dept: res.dept,
                course: ""
            })
        })

    function getFutureCourses(){

        const futureCourseURL = 'http://localhost:5000/futureCourse';
        const futureCourseOptions = {
            method: 'GET',
            headers: {
              'Accept': 'application/json',
            }
        };

        fetch(futureCourseURL, futureCourseOptions)
        .then(res => res.json())
        .then(res => {
            setCourseList(res);
        })

    }




    function handleChange(event){

        const {name, value} = event.target;

        setNewValue(prevValue => {
            return{
                ...prevValue,
                [name]: value
            }; 
        });

        console.log(newValue);
    }

    return (
        <div>
        <Header />
    <Form className="inputForm">
    <Container>
    <Row>
        <Col lg={5}> 
            <Form.Control placeholder={fields[0]} size="lg" onChange={handleChange} value={newValue.username} name="username"></Form.Control>
        </Col>
        <Col lg={{ span: 5, offset: 2 }} >
        <InputGroup>
            <Form.Control placeholder={fields[1]} size="lg" name="email" type="email" onChange={handleChange} value={newValue.email} className="input"></Form.Control>
            <InputGroup.Append>
                <InputGroup.Text>@hohsc.org.hk</InputGroup.Text>
            </InputGroup.Append>
        </InputGroup> 

        </Col>
    </Row>
    <Row>
        <Col lg={5}>
            <Form.Control placeholder={fields[2]} size="lg" onChange={handleChange} value={newValue.staffid} name="staffid"></Form.Control>
        </Col>
        <Col lg={{span: 5, offset: 2 }}>
            <Form.Control placeholder={fields[3]} size="lg" onChange={handleChange} value={newValue.post} name="post"></Form.Control>
        </Col>
    </Row>
    <Row>
        <Col lg={5}>
            <Form.Control placeholder={fields[4]} size="lg" onChange={handleChange} value={newValue.dept} name="dept"></Form.Control>
        </Col>
        <Col lg={{span: 5, offset: 2 }}>
            <Form.Control placeholder={fields[5]} size="lg" onClick={getFutureCourses} onChange={handleChange} value={newValue.course} name="course" as="select">
                {couseList.forEach(course => {
                    return <option> {course.courseName} </option>
                })}
            </Form.Control>
        </Col>
    </Row>
    <Row className="submitBtn">
        <Col lg={{span: 2, offset:5}}>
            <Button variant="primary" size="lg" type="submit">提交</Button>
        </Col>
    </Row>
    </Container>
    </Form>
    <Footer role={role}/>
    </div>
    );
}

export default ApplicationForm;
