import React, { useState } from 'react';
import qs from 'qs';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import { Redirect } from 'react-router-dom';

export default function CourseDetail(){

    const courseSelectedStr = localStorage.getItem('courseSelected');
    const courseSelected = qs.parse(courseSelectedStr);

    const date = new Date(courseSelected.date);

    const [getCourseDetail, setGetCourseDetail] = useState(null);

    function handleClick(){

      const courseID = courseSelected._id;

      const url = 'http://localhost:5000/enrollDetail';
        const options = {
            method: 'POST',
            headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/x-www-form-urlencoded'
            },
            body: qs.stringify({courseID: courseID})
        };

        fetch(url, options)
        .then(res => res.json())
        .then(res => {
            if(res.message === "未有報名記錄"){
              alert(res.message)
            }else{
              setGetCourseDetail(res);
            }
        })

    }


    return(
        <div>
            {console.log(getCourseDetail)}
            <Card className="courseCard">
              <Card.Header as="h3" className="courseTitle">{courseSelected.courseName}</Card.Header>
              <Card.Body>
                <Card.Title className="courseDescription">{courseSelected.description}</Card.Title>
                <Card.Text className="courseInfo">上課日期: {date.toLocaleDateString()} {courseSelected.time}</Card.Text>
                <Card.Text className="courseInfo">上課地點: {courseSelected.venue}</Card.Text>
                <Card.Text className="courseInfo">課程導師: {courseSelected.tutor}</Card.Text>
                <Card.Text className="courseInfo">尚餘名額: {courseSelected.quota}</Card.Text>
                <Button variant="primary" className="adminEnrollRecordBtn" onClick={handleClick}>查看報名情況</Button>
              </Card.Body>
              {getCourseDetail && <Redirect to="/enroll-detail"/> }
            </Card>


        </div>
    );

}
