import React, { useState } from 'react';
import qs from 'qs';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

export default function CourseDetail(){



    const courseSelectedStr = localStorage.getItem('courseSelected');
    const courseSelected = qs.parse(courseSelectedStr);
    const [course, setCourse] = useState(courseSelected)

    function onStorageEvent(storageEvent){

        console.log("inside storage event")

        alert("storage event");
    }

    window.addEventListener('storage', onStorageEvent, false);

    console.log(courseSelected);

    return(
        <div>

            <Card className="courseCard">
              <Card.Header as="h3" className="courseTitle">{courseSelected.courseName}</Card.Header>
              <Card.Body>
                <Card.Title className="courseDescription">{courseSelected.description}</Card.Title>
                <Card.Text className="courseInfo">上課日期: {courseSelected.date} {courseSelected.time}</Card.Text>
                <Card.Text className="courseInfo">上課地點: {courseSelected.venue}</Card.Text>
                <Card.Text className="courseInfo">課程導師: {courseSelected.tutor}</Card.Text>
                <Card.Text className="courseInfo">尚餘名額: {courseSelected.quota}</Card.Text>
                <Button variant="primary" className="adminEnrollRecordBtn">查看報名情況</Button>
              </Card.Body>
            </Card>

        </div>
    );

}
