import React from "react";
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container'
import Input from "./Input";


function Form(){

    const fields = ["姓名", "電郵", "職員編號", "職位", "單位", "報讀課程"];

    return <form className="inputForm">
    <Container>
    <Row>
        <Col md={12} lg={5}> <Input fieldName={fields[0]}/> </Col>
        <Col md={12} lg={{ span: 5, offset: 1 }} > <Input fieldName={fields[1]}/> </Col>
    </Row>
    <Row>
        <Col md={12} lg={5}> <Input fieldName={fields[2]}/> </Col>
        <Col md={12} lg={{span: 5, offset: 1 }}> <Input fieldName={fields[3]}/> </Col>
    </Row>
    <Row>
        <Col md={{span:2, offset:3}} lg={5}> <Input fieldName={fields[4]}/> </Col>
        <Col md={12} lg={{span: 5, offset: 1 }}> <Input fieldName={fields[5]}/> </Col>
    </Row>
    <Row className="submitBtn">
        <Col md={{span:4, offset:5}}lg={{span: 2, offset:4}}>
            <Button variant="primary large" type="submit">提交</Button>
        </Col>
    </Row>
    </Container>


    </form>;
}

export default Form;

