import React, {useState, useEffect} from 'react';
import {Redirect} from 'react-router-dom';
import Header from './Header';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/Button';
import RegisterRecordDetail from './RegisterRecordDetail';
import qs from 'qs';

export default function RegisterRecord(){

    const initialCourse = [{
        _id: "",
        couseName: "",
        date: "",
        time: "",
        venue: "",
        quota: 0,
        tutor: "",
        description: ""
    }];

    const[courses, setCourses] = useState(initialCourse);
    const [selectedID, setSelectedID] = useState();
    const [period, setPeriod] = useState("今天");
    const [isLoaded, setIsLoaded] = useState(false);
    const [clearDetail, setClearDetail] = useState(true);

    useEffect(() => {

        if(!isLoaded){
            console.log("render");
            const url = 'http://localhost:5000/course-by-date';
            const options = {
                method: 'POST',
                headers: {
                  'Accept': 'application/json',
                  'Content-Type': 'application/x-www-form-urlencoded'
                },
                body: qs.stringify({
                    period: period
                })
            };

            fetch(url, options)
            .then(res => res.json())
            .then(res => {
                if(res !== courses){
                    setCourses(res);
                }
            })
            setIsLoaded(true);
        }
    })

    function handleClick(selectedKey){
        console.log(selectedKey);
        switch(selectedKey){
            case "0":
                setPeriod("過往");
                setIsLoaded(false);
                setClearDetail(true);
                break;
            case "1":
                setPeriod("今天");
                setIsLoaded(false);
                setClearDetail(true);
                break;
            case "2":
                setPeriod("未來");
                setIsLoaded(false);
                setClearDetail(true);
                break;
            default:
                break;
        }

    }

    return(
        <React.Fragment>
        <Navbar bg="primary" variant="dark">
            <Navbar.Brand href="/">靈實員工培訓課程報名系統</Navbar.Brand>
            <Nav className="mr-auto" defaultActiveKey="" onSelect={(selectedKey) => handleClick(selectedKey)}>
                <Nav.Link eventKey="0" >過往課程</Nav.Link>
                <Nav.Link eventKey="1" >今天課程</Nav.Link>
                <Nav.Link eventKey="2" >末來課程</Nav.Link>
            </Nav>
        </Navbar>
        <div className="sidebar">

            <h4 className="sidebarTitle">{period}課程</h4>

            <Nav defaultActiveKey="" className="flex-column" onSelect={(selectedKey) => setSelectedID(selectedKey)}>
                {courses.map(course => {
                    const date = new Date(course.date);
                    return <Nav.Link eventKey={course._id} className="courseLink">{course.courseName} {date.toLocaleDateString()}</Nav.Link>
                })}
            </Nav>

        </div>

        {selectedID && <RegisterRecordDetail selectedCourse={courses.find(course => course._id === selectedID)}/>}

        </React.Fragment>
    );

}
