import React, { useState, useEffect } from 'react';
import Nav from 'react-bootstrap/Nav';

var id = "";

function Sidebar(props){

    const [selectedID, setSelectedID] = useState("");

    const courses = props.courses;

    useEffect(() => {
        id = selectedID;
    })

    return (
        <div className="sidebar">
            <h4 className="sidebarTitle">選擇課程</h4>

            <Nav defaultActiveKey="/home" className="flex-column" onSelect={(selectedKey) => setSelectedID(selectedKey)}>

            {courses.map(course => {
                return <Nav.Link eventKey={course._id}>{course.courseName}</Nav.Link>
            })}

            </Nav>

        </div>
    );

}

export default { Sidebar, id };
