import React, { useState, useEffect } from 'react';
import Card from 'react-bootstrap/Card';
import Table from 'react-bootstrap/Table';

export default function EnrollDetail(props){

    console.log(props.courseDetail);
    const [enrollRecords, setEnrollRecords] = useState([]);
    const [waitinglist, setWaitingList] = useState([]);

    useEffect(() => {
        setEnrollRecords(props.courseDetail.records);
        setWaitingList(props.courseDetail.waitinglist);
    })

    return (

        <Card className="courseDetailCard">
          <Card.Header className="courseDetailTitle">{props.courseDetail.courseName}</Card.Header>
          <Card.Body>
                <Table striped bordered hover>
                    <thead>
                      <tr>
                        <th>職員編號</th>
                        <th>姓名</th>
                        <th>電郵</th>
                        <th>單位</th>
                        <th>職位</th>
                      </tr>
                    </thead>
                    <tbody>

                         {enrollRecords && enrollRecords.map(record => {
                            return (
                                <tr>
                                    <td>{record.staffid}</td>
                                    <td>{record.username}</td>
                                    <td>{record.email}</td>
                                    <td>{record.dept}</td>
                                    <td>{record.post}</td>
                                </tr>
                            );
                        })}

                    </tbody>
                </Table>
                <p>總報名人數： {enrollRecords.length}</p>
          </Card.Body>
        </Card>

    );
}
