import React, { useState } from "react";
import auth from "./Auth";
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from "react-bootstrap/Col";
import InputGroup from 'react-bootstrap/InputGroup';
import Button from 'react-bootstrap/Button';
import { Link, Redirect } from 'react-router-dom';
import qs from 'qs';
import axios from "axios";

function Login() {

    const [userData, setUserData] = useState({

        username: "",
        password: ""

    });

    const [redirect, setRedirect] = useState({
        to: "",
        isDirected: null
    });

    const [reLogin, setRelogin] = useState(false);

    axios.get("http://localhost:5000/isLogged", {withCredential: true})
        .then(res => {
            console.log(res);
    });

    if(localStorage.getItem('isAuthenticated') == true){
        auth.login( ()=>{
            console.log("Login Page: Login");
        })
        return <Redirect to="/" />;
    }

    function handleChange(event){

        const {name, value} = event.target;

        setUserData(prevValue => {
            return {
                ...prevValue,
                [name]: value
            };
        });

        console.log(userData);
    }

    function handleSubmit(event){

        const user = {
            username: userData.username,
            password: userData.password
        }

        event.preventDefault();

        axios.post("http://localhost:5000/login", qs.stringify(user), {
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded'
            }
        })
        .then(res => {
            if(res.data){
                localStorage.setItem('isAuthenticated', true);
                setRedirect({
                    to: "/",
                    isDirected: true
                });
            }
        })
        .catch(err => {
            console.log(err.response);
        })

    }

    return <div>
    <Row>
        <Col md={{span: 8, offset: 2}}lg={{span: 6, offset: 4}}>
        <img src="https://www.hohcs.org.hk/App/Modules/Admin/Tpl/Static/upload/image/20170418/20170418125117_21846.png" className="icon"/>
        </Col>
    </Row>
    { reLogin && <p className="loginError">登入電郵或密碼錯誤，請重新輸入</p> }
    <Row>
    <Col md={{span: 8, offset: 2}} lg={{span: 6, offset: 3}}>
    {/* onSubmit={handleSubmit} */}
        <Form action="http://localhost:5000/login" method="post" onSubmit={handleSubmit}>
            <Row>
                <Col lg={{span: 4, offset: 4}}>
                    <InputGroup>
                        <Form.Control placeholder="電郵" className="input" name="username" onChange={handleChange} value={userData.username}></Form.Control>
                        <InputGroup.Append>
                            <InputGroup.Text>@hohsc.org.hk</InputGroup.Text>
                        </InputGroup.Append>
                    </InputGroup> 
                </Col>
            </Row>
            <Row>
                <Col lg={{span: 4, offset: 4}}>
                    <Form.Group>
                        <Form.Control placeholder="密碼" onChange={handleChange} name="password" value={userData.password} type="password"></Form.Control>
                    </Form.Group>
                </Col>
            </Row>

            <Row>
            {/* onClick={handleLogin} */}
                <Button variant="primary" size="lg" className="loginBtn" type="submit">登入</Button>
            </Row>
            <p className="remindText">如未曾註冊，請先註冊。</p>
            <Row>

                <Link className="registerBtn" to="/register">
                    <Button variant="secondary" size="lg" >註冊</Button>
                </Link>


            </Row>




        </Form>
        {redirect.isDirected && <Redirect to={redirect.to} /> }
    </Col>
    </Row>
    </div>;

}

export default Login;
