import React, {useState, useEffect} from 'react';
import Card from 'react-bootstrap/Card';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';

export default function WaitingList(props){

    const courseID = props.courseID;

    const [records, setRecords] = useState([{}]);

    useEffect(() => {

        const url = 'http://localhost:5000/waitinglistByCourseID';
        const options = {
            method: 'POST',
            headers: {
              'Accept': 'application/json'
            },
            body: {
                courseID: courseID
            }
        };

        fetch(url, options)
        .then(res => res.json())
        .then(res => {
            if(records.length === 0){
                setRecords(res);
            }
        })
    })




    return (

        <Card className="detailCard">
          <Card.Header>
            <Button className="backBtn" onClick={backBtnClicked}>返回查看報名名單</Button>
            <h1 className="enrollDetailTitle">{records.courseName}</h1>
        </Card.Header>
          <Card.Body>
                <Table striped bordered hover>
                    <thead>
                      <tr>
                        <th>職員編號</th>
                        <th>姓名</th>
                        <th>電郵</th>
                        <th>單位</th>
                        <th>職位</th>
                      </tr>
                    </thead>
                    <tbody>

                         {records.length !== 0 && records.map(record => {
                            return (
                                <tr>
                                    <td>{record.staffid}</td>
                                    <td>{record.username}</td>
                                    <td>{record.email}</td>
                                    <td>{record.dept}</td>
                                    <td>{record.post}</td>
                                </tr>
                            );
                        })}

                    </tbody>
                </Table>
                <p>總後補人數： { records && records.length }</p>
          </Card.Body>
        </Card>

    ); 

}
