import React, { useState } from "react";
import ListGroup from 'react-bootstrap/ListGroup'
import Nav from 'react-bootstrap/Nav';
import qs from 'qs';




export default function CourseList(){

    const initialCourse = [{
        _id: "",
        couseName: "",
        date: "",
        time: "",
        venue: "",
        quota: 0,
        tutor: "",
        description: ""
    }];

    const [courseSelected, setCourseSelected] = useState({});

    const[courses, setCourses] = useState(initialCourse)

    const url = 'http://localhost:5000/course';
        const options = {
            method: 'GET',
            headers: {
              'Accept': 'application/json',
            },
        };

        fetch(url, options)
        .then(res => res.json())
        .then(res => {
            setCourses(res);
        })

    function handleClick(event){
        console.log(event.target.textContent);

        const courseName = event.target.textContent;

        for (const course of courses) {
            if(course.courseName === courseName){
                setCourseSelected(course);
                localStorage.setItem("courseSelected", qs.stringify(course));
                break;
            }
        }

    }

     /* <ListGroup>

        </ListGroup> */


    return(

        <Nav defaultActiveKey="/home" className="flex-column">
            {courses.map(course => {
                return <Nav.Link key={course._id} href="/cousedetail" onClick={handleClick}>{course.courseName}</Nav.Link>
            })}
            <Nav.Link href="/home">Active</Nav.Link>
            <Nav.Link eventKey="link-1">Link</Nav.Link>
            <Nav.Link eventKey="link-2">Link</Nav.Link>
            <Nav.Link eventKey="disabled" disabled>
              Disabled
            </Nav.Link>
        </Nav>


    );

}
