import React from "react";
import Button from 'react-bootstrap/Button';
import Input from "./Input";

function Form(){

    const fields = ["姓名", "電郵", "職員編號", "職位", "單位", "報名課程"];

    return <form className="inputForm">
        {fields.map(field => {
            return <Input fieldName={field}/>;
        })}
        <Button variant="primary" type="submit">提交</Button>
    </form>;
}

export default Form;
