import React, {useState} from "react";
import Header from "./Header";
import Footer from "./Footer";
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import axios from 'axios';
import qs from 'qs';
import auth from './Auth';


function ApplicationForm(){

    console.log("Application form: " + auth.isAuthenticated())

    const [newValue, setNewValue] = useState({
        username: "",
        email: "",
        staffid: "",
        post: "",
        dept: "",
        course: ""
    });

    axios.post("http://localhost:5000/isAuthenticated", { token: localStorage.getItem('userToken') })
        .then(res => {
            console.log(res);
    });

    // const url = 'http://localhost:5000/isAuthenticated';
    //     const options = {
    //         method: 'POST',
    //         headers: {
    //           'Accept': 'application/json',
    //           
    //           'Access-Control-Allow-Origin': 'http://localhost:3000',
    //           'Access-Control-Allow-Credentials': 'true'
    //         },
    //         body: qs.stringify()
    //     };

    //     fetch(url, options, { credentials: 'same-origin' })
    //     .then(res => {
    //         console.log(res);
    //     });

    function handleChange(event){

        const {name, value} = event.target;

        setNewValue(prevValue => {
            return{
                ...prevValue,
                [name]: value
            }; 
        });

        console.log(newValue);
    }

    const fields = ["姓名", "電郵", "職員編號", "職位", "單位", "報讀課程"];


    return (
        <div>
        <Header />
    <Form className="inputForm">
    <Container>
    <Row>
        <Col lg={5}> 
            <Form.Control placeholder={fields[0]} size="lg" onChange={handleChange} value={newValue.username} name="username"></Form.Control>
        </Col>
        <Col lg={{ span: 5, offset: 2 }} >
        <InputGroup>
            <Form.Control placeholder={fields[1]} size="lg" name="email" type="email" onChange={handleChange} value={newValue.email} className="input"></Form.Control>
            <InputGroup.Append>
                <InputGroup.Text>@hohsc.org.hk</InputGroup.Text>
            </InputGroup.Append>
        </InputGroup> 

        </Col>
    </Row>
    <Row>
        <Col lg={5}>
            <Form.Control placeholder={fields[2]} size="lg" onChange={handleChange} value={newValue.staffid} name="staffid"></Form.Control>
        </Col>
        <Col lg={{span: 5, offset: 2 }}>
            <Form.Control placeholder={fields[3]} size="lg" onChange={handleChange} value={newValue.post} name="post"></Form.Control>
        </Col>
    </Row>
    <Row>
        <Col lg={5}>
            <Form.Control placeholder={fields[4]} size="lg" onChange={handleChange} value={newValue.dept} name="dept"></Form.Control>
        </Col>
        <Col lg={{span: 5, offset: 2 }}>
            <Form.Control placeholder={fields[5]} size="lg" onChange={handleChange} value={newValue.course} name="course"></Form.Control>
        </Col>
    </Row>
    <Row className="submitBtn">
        <Col lg={{span: 2, offset:5}}>
            <Button variant="primary" size="lg" type="submit">提交</Button>
        </Col>
    </Row>
    </Container>
    </Form>
    <Footer />
    </div>
    );
}

export default ApplicationForm;
