import React, {useState, useEffect} from 'react';
import {Redirect} from 'react-router-dom';
import Header from './Header';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/Button';

export default function RegisterRecord(){

    const initialCourse = [{
        _id: "",
        couseName: "",
        date: "",
        time: "",
        venue: "",
        quota: 0,
        tutor: "",
        description: ""
    }];

    const[courses, setCourses] = useState(initialCourse);
    const [selectedID, setSelectedID] = useState("");
    const [courseToParse, setCourseToParse] = useState(initialCourse[0]);

    useEffect(() => {

        const url = 'http://localhost:5000/adminTodayCourse';
        const options = {
            method: 'GET',
            headers: {
              'Accept': 'application/json',
            },
        };

        fetch(url, options)
        .then(res => res.json())
        .then(res => {
            if(courses === initialCourse){
                setCourses(res);
            }
        })

        setCourseToParse(courses.find(course => course._id === selectedID));
    })

    return(
        <React.Fragment>
        <Navbar bg="primary" variant="dark">
            <Navbar.Brand href="/">靈實員工培訓課程報名系統</Navbar.Brand>
            <Nav className="mr-auto">
                <Nav.Link href="/register-record">過往課程</Nav.Link>
                <Nav.Link href="/register-record">末來課程</Nav.Link>
            </Nav>
        </Navbar>
        <div className="sidebar">

            <h4 className="sidebarTitle">今天課程</h4>

            <Nav defaultActiveKey="" className="flex-column" onSelect={(selectedKey) => console.log(selectedKey)}>

            </Nav>

        </div>
        </React.Fragment>
    );

}
