import React, {useState} from "react";
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container'
import Form from 'react-bootstrap/Form'


function ApplicationForm(){

    const [newValue, setNewValue] = useState("");

    function handleChange(event){

        const inputFields = event.target;

        console.log(inputFields.name);

        // setNewValue(input);

        // console.log(newValue);
    }

    const fields = ["姓名", "電郵", "職員編號", "職位", "單位", "報讀課程"];


    return <Form className="inputForm">
    <Container>
    <Row>
        <Col lg={5}> 
            <Form.Control placeholder={fields[0]} size="lg" onChange={handleChange} value={newValue} name="username"></Form.Control>
        </Col>
        <Col lg={{ span: 5, offset: 2 }} > 
            <Form.Control placeholder={fields[1]+ ": abc@hohcs.org.hk"} size="lg" name="email" type="email"></Form.Control>
        </Col>
    </Row>
    <Row>
        <Col lg={5}>
            <Form.Control placeholder={fields[2]} size="lg" onChange={handleChange} value={newValue} name="staffid"></Form.Control>
        </Col>
        <Col lg={{span: 5, offset: 2 }}>
            <Form.Control placeholder={fields[3]} size="lg" onChange={handleChange} value={newValue} name="post"></Form.Control>
        </Col>
    </Row>
    <Row>
        <Col lg={5}>
            <Form.Control placeholder={fields[4]} size="lg" onChange={handleChange} value={newValue} name="dept"></Form.Control>
        </Col>
        <Col lg={{span: 5, offset: 2 }}>
            <Form.Control placeholder={fields[5]} size="lg"></Form.Control>
        </Col>
    </Row>
    <Row className="submitBtn">
        <Col lg={{span: 2, offset:5}}>
            <Button variant="primary" size="lg" type="submit">提交</Button>
        </Col>
    </Row>
    </Container>


    </Form>;
}

export default ApplicationForm;
