import React, {useState} from 'react';
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Navigator from './Navigator';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import CourseDetail from './CourseDetail';
import ListGroup from 'react-bootstrap/ListGroup'
import Nav from 'react-bootstrap/Nav';
import qs from 'qs';



export default function Admin(){

    const initialCourse = [{
        _id: "",
        couseName: "",
        date: "",
        time: "",
        venue: "",
        quota: 0,
        tutor: "",
        description: ""
    }];

    const [courseSelected, setCourseSelected] = useState({});

    const[courses, setCourses] = useState(initialCourse)

    const url = 'http://localhost:5000/course';
        const options = {
            method: 'GET',
            headers: {
              'Accept': 'application/json',
            },
        };

        fetch(url, options)
        .then(res => res.json())
        .then(res => {
            setCourses(res);
        })

    function handleClick(event){
        console.log(event.target.textContent);

        document.getElementsByClassName('linkSelected')[0].classList.remove('linkSelected');
        event.target.classList.add('linkSelected');

        const courseName = event.target.textContent;

        for (const course of courses) {
            if(course.courseName === courseName){
                setCourseSelected(course);
                localStorage.setItem("courseSelected", qs.stringify(course));
                break;
            }
        }

    }


    return (
    <Router>
        <Navigator />
        <Row>
            <Col lg={2} className="courseList">
                <Nav defaultActiveKey="/home" className="flex-column">
                    {courses.map(course => {
                        return <Nav.Link key={course._id} onClick={handleClick} className="courseLink">{course.courseName}</Nav.Link>
                    })}
                </Nav>
            </Col>

            <Col lg={10} className="courseDetail">

                    <CourseDetail />

            </Col>

        </Row>
    </Router>
    );
}
