import React, { useState, useEffect } from 'react';
import Nav from 'react-bootstrap/Nav';

export default function Sidebar(props){

    const [selectedID, setSelectedID] = useState("");

    const courses = props.courses;

    return (
        <div className="sidebar">
            <h4 className="sidebarTitle">選擇課程</h4>

            {/* onSelect={(selectedKey) => setSelectedID(selectedKey)} */}

            <Nav defaultActiveKey="/home" className="flex-column" onSelect={(selectedKey) => setSelectedID(selectedKey)}>

            {courses.map(course => {
                return <Nav.Link eventKey={course._id} to="/enroll-detail">{course.courseName}</Nav.Link>
            })}

            </Nav>

        </div>
    );

}
