import React, { useState } from "react";
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from "react-bootstrap/Col";
import InputGroup from 'react-bootstrap/InputGroup';
import Button from 'react-bootstrap/Button';
import { Link, Redirect } from 'react-router-dom';
import axios from "axios";

function Login() {

    const [userData, setUserData] = useState({

        username: "",
        password: ""

    });

    const [redirect, setRedirect] = useState(false);

    function handleChange(event){

        const {name, value} = event.target;

        setUserData(prevValue => {
            return {
                ...prevValue,
                [name]: value
            };
        });

        console.log(userData);
    }

    function handleLogin(){

        axios.post("http://localhost:5000/login", {
            username: userData.username,
            password: userData.password
        },{
            headers: {
                'Content-Type': 'application/json'
              }
        })
        .then(res => {
           if(res.data === "Login info matches"){
               setRedirect(true);
               console.log(redirect);
           }else{
               userData = null;
           }
        })

    }

    return <div>
    <Row>
        <Col md={{span: 8, offset: 2}}lg={{span: 6, offset: 4}}>
        <img src="https://www.hohcs.org.hk/App/Modules/Admin/Tpl/Static/upload/image/20170418/20170418125117_21846.png" className="icon"/>
        </Col>
    </Row>
    <Row>
    <Col md={{span: 8, offset: 2}} lg={{span: 6, offset: 3}}>
        <Form >
            <Row>
                <Col lg={{span: 4, offset: 4}}>
                    <InputGroup>
                        <Form.Control placeholder="電郵" className="emailInput" name="username" onChange={handleChange} value={userData.username}></Form.Control>
                        <InputGroup.Append>
                            <InputGroup.Text>@hohsc.org.hk</InputGroup.Text>
                        </InputGroup.Append>
                    </InputGroup> 
                </Col>
            </Row>
            <Row>
                <Col lg={{span: 4, offset: 4}}>
                    <Form.Group>
                        <Form.Control placeholder="密碼" onChange={handleChange} name="password" value={userData.password} type="password"></Form.Control>
                    </Form.Group>
                </Col>
            </Row>
                <Button variant="primary" size="lg" className="loginBtn" onClick={handleLogin}>登入</Button>

                <Button variant="secondary" size="lg" className="registerBtn">註冊</Button>
        </Form>
    </Col>
    </Row>
    </div>;

}

export default Login;
