import React from "react";
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container'
import Form from 'react-bootstrap/Form'
import FormControl from 'react-bootstrap/Form'
import Input from "./Input";


function ApplicationForm(){

    const fields = ["姓名", "電郵", "職員編號", "職位", "單位", "報讀課程"];

    return <Form className="inputForm">
    <Container>
    <Row>
        <Col lg={5}> <Form.Control placeholder={fields[0]}></Form.Control> </Col>
        <Col lg={{ span: 5, offset: 1 }} > <Form.Control placeholder={fields[1]}></Form.Control> </Col>
    </Row>
    <Row>
        <Col lg={5}> <Form.Control placeholder={fields[2]}></Form.Control> </Col>
        <Col lg={{span: 6, offset: 1 }}> <Form.Control placeholder={fields[3]}></Form.Control> </Col>
    </Row>
    <Row>
        <Col lg={5}> <Form.Control placeholder={fields[4]}></Form.Control> </Col>
        <Col lg={{span: 5, offset: 1 }}> <Form.Control placeholder={fields[5]}></Form.Control></Col>
    </Row>
    <Row className="submitBtn">
        <Col lg={{span: 2, offset:4}}>
            <Button variant="primary" type="submit">提交</Button>
        </Col>
    </Row>
    </Container>


    </Form>;
}

export default ApplicationForm;
