import React from "react";
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Input from "./Input";


function Form(){

    const fields = ["姓名", "電郵", "職員編號", "職位", "單位", "報讀課程"];

    return <form className="inputForm">
    <Row>
        <Col md={{ span: 6, offset: 0 }}> <Input fieldName={fields[0]}/> </Col>
        <Col > <Input fieldName={fields[1]}/> </Col>
    </Row>
    <Row>
        <Col md={{ span: 6, offset: 0 }}> <Input fieldName={fields[2]}/> </Col>
        <Col> <Input fieldName={fields[3]}/> </Col>
    </Row>
    <Row>
        <Col md={{ span: 6, offset: 0 }}> <Input fieldName={fields[4]}/> </Col>
        <Col> <Input fieldName={fields[5]}/> </Col>
    </Row>
        <Button variant="primary" type="submit" className="submitBtn">提交</Button>
    </form>;
}

export default Form;

