import React, {useState, useEffect} from 'react';
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { AdminRoute } from '../admin-route';
import Navigator from './Navigator';
import EnrollDetail from './EnrollDetail';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import CourseDetail from './CourseDetail';
import ListGroup from 'react-bootstrap/ListGroup'
import Sidebar from './SideBar';
import Nav from 'react-bootstrap/Nav';
import qs from 'qs';



export default function Admin(){

    const initialCourse = [{
        _id: "",
        couseName: "",
        date: "",
        time: "",
        venue: "",
        quota: 0,
        tutor: "",
        description: ""
    }];

    const[courses, setCourses] = useState(initialCourse);
    const [selectedID, setSelectedID] = useState("");

    useEffect(() => {

        const url = 'http://localhost:5000/course';
        const options = {
            method: 'GET',
            headers: {
              'Accept': 'application/json',
            },
        };

        fetch(url, options)
        .then(res => res.json())
        .then(res => {
            if(courses === initialCourse){
                setCourses(res);
            }
        })
    })



    function handleClick(event){
        console.log(event.target.textContent);

        var element = document.getElementsByClassName('linkSelected')[0]

        if(element){
            element.classList.remove('linkSelected');
        }
        event.target.classList.add('linkSelected');

        const courseName = event.target.textContent;

        for (const course of courses) {
            if(course.courseName === courseName){
                localStorage.setItem("courseSelected", qs.stringify(course));
                break;
            }
        }

    }


    return (
        <div>
            <Navigator />
            <div className="sidebar">

                <h4 className="sidebarTitle">選擇課程</h4>

                <Nav defaultActiveKey="/home" className="flex-column" onSelect={(selectedKey) => setSelectedID(selectedKey)}>

                    {courses.map(course => {
                        return <Nav.Link eventKey={course._id} href="/enroll-detail">{course.courseName}</Nav.Link>
                    })}

                </Nav>

            </div>

        {/* <Row>
            <Col lg={2} className="courseList">
                <Nav defaultActiveKey="/home" className="flex-column">
                    {courses.map(course => {
                        return <Nav.Link key={course._id} onClick={handleClick} className="courseLink">{course.courseName}</Nav.Link>
                    })}
                </Nav>
            </Col>

            <Col lg={10} className="courseDetail">

                    <CourseDetail />

            </Col>

        </Row> */}
    </div>
    );
}
