import React, { useState } from "react";
import ListGroup from 'react-bootstrap/ListGroup'




export default function CourseList(){

    const initialCourse = [{
        _id: "",
        couseName: "",
        date: "",
        time: "",
        venue: "",
        quota: 0,
        tutor: "",
        description: ""
    }];

    const [courseSelected, setCourseSelected] = useState({});

    const[courses, setCourses] = useState(initialCourse)

    const url = 'http://localhost:5000/course';
        const options = {
            method: 'GET',
            headers: {
              'Accept': 'application/json',
            },
        };

        fetch(url, options)
        .then(res => res.json())
        .then(res => {
            setCourses(res);
        })

    function handleClick(event){
        console.log(event.target.textContent);

        const courseName = event.target.textContent;

        for (const course of courses) {
            if(course.courseName === courseName){
                setCourseSelected(course);
                //export { courseSelected };
                break;
            }
        }

    }


    return(
        <ListGroup>
            {courses.map(course => {
                return <ListGroup.Item key={course._id} onClick={handleClick}>{course.courseName}</ListGroup.Item>
            })}
        </ListGroup>
    );

}
